<?phpglobal $wpdb;global $kianland_core;if(isset($_POST['kc_delete_device'])){    $id = $_POST['kc_device_id'];    $wpdb->query("DELETE FROM `wp_prweb_devices` WHERE id='$id'");    echo $wpdb->last_error;}$results = $wpdb->get_results("SELECT * FROM `wp_prweb_devices`");?>    <div class="kc-wrapper">        <h1 class="kc-header-area">دستگاه های ثبت شده</h1>        <table id="devicesTable" class="display">            <thead>            <tr>                <th>ردیف</th>                <th>شماره سریال دستگاه</th>                <th>تاریخ اتمام گارانتی</th>                <th>تاریخ ساخت دستگاه</th>                <th>نوع دستگاه</th>                <th>مدل دستگاه</th>                <th>نام خریدار</th>                <th>ویرایش</th>                <th>حذف</th>            </tr>            </thead>            <tbody>        <?php        $counter = 1;        if ($results) {            foreach ($results as $result) {                ?>                <tr>                    <td class="kc-devices-counter">                        <?= $counter ?>                    </td>                    <td class="kc-devices-counter"><?= $result->device_id ?></td>                    <td class="kc-devices-counter"><?= timeStampToShamsiWithoutTime($result->device_expire_date, 'a') ?></td>                    <td class="kc-devices-counter"><?= timeStampToShamsiWithoutTime($result->device_build_date, 'a') ?></td>                    <td class="kc-devices-counter"><?= $result->device_spec ?></td>                    <td class="kc-devices-counter"><?= $result->device_model ?></td>                    <td class="kc-devices-counter"><?= $result->device_owner ?></td>                    <td class="kc-devices-counter">                        <form action="https://maadsteel.co/wp-admin/admin.php?page=prweb_edit_device" method="post" class="kc-device-edit-form">                            <input type="text" name="kc_id_sender" value="<?= $result->id ?>" class="kc-hidden-element">                            <button type="submit" class="kc-button-3 kc-button-round"><span class="dashicons dashicons-edit"></span></button>                        </form>                    </td>                    <td class="kc-devices-counter">                        <form action="" method="post" class="kc-device-edit-form">                            <input type="text" name="kc_device_id" value="<?= $result->id ?>" class="kc-hidden-element">                            <button type="submit" class="kc-button-3 kc-button-round" name="kc_delete_device"><span class="dashicons dashicons-trash"></span></button>                        </form>                    </td>                </tr>                <?php                $counter++;            }        }        ?>            </tbody>        </table>    </div><?php